import React, {useState} from 'react';
import {  useSelector } from 'react-redux';
import MyProductListItem from '../mycomponents/MyProductListItem';
import { useNavigate, useParams } from "react-router-dom"
import { MyProductList } from '../mydata/MyProductList'; 

export default function MyCheckout() {
    const params = useParams();
    const list = useSelector((state) => state.cart.list);
    const [state,setState] = useState(
        params.id
            ? [
                {
                    ...MyProductList.find(
                        (element) => element.id === parseInt(params.id)
                    ),
                    count: 1,
                },
            ]
            : list
    );


    const navigate = useNavigate();

    const incrementItem = (item) => {
        const index = state.findIndex((product) => product.id === item.id
        );
        setState((state) => [
        ...state.slice(0,index),
        {...item, count: item.count + 1},
        ...state.slice(index + 1),
        ]);
    };
    const decrementItem = (item) => {
        if (item.count === 1) {
            removeItemFromCart(item);
        } else {
            const index = state.findIndex((product) => product.id === item.id
            );
            setState((state) => [
            ...state.slice(0,index),
            {...item, count: item.count - 1},
            ...state.slice(index + 1),
            ]);
        }
    };
    const removeItemFromCart = (item) => {
        const index = state.findIndex((product) => product.id === item.id
        );
        setState((state) => [
        ...state.slice(0,index),
        ...state.slice(index + 1)
        ]);
    };

    return (
        <>
            {state.length > 0 ? (
                <>
                    {state.map((item) => (
                        <MyProductListItem
                            {...item}
                            key={item.id}
                            incrementItem={() => incrementItem(item)}
                            decrementItem={() => decrementItem(item)}
                            removeItem={() => removeItemFromCart(item)}

                        />
                    ))}
                    <button
                        className='btn btn-success'
                        onClick={() => navigate('/success')}
                    >
                        place order
                    </button>
                </>
            ) : (
                <h3>No item in the Checkout </h3>
            )}

        </>
    );

}
